import { Link } from "react-router-dom";

function FriendList(props) {
  const FriendList = props.friends.map((friend) => (
    <div className="friend">
      <div>
        <img class="avatar" src={friend.avatar} />
        <span className="title">{friend.name}</span>
      </div>
      <span className="cost">+{friend.income.toFixed(2)} ₽</span>
    </div>
  ));

  return <div className="FriendList">{FriendList}</div>;
}

